   background: url("/f4aaddd52e8cc952fe28649369755dd8.jpg") no-repeat center;
   background-size: cover;
}

body::before {
   content: "";
   display: block;
   position: absolute;
   top: 0;
   left: 0;
   width: 100%;
   height: 100%;
   background: #0000009c;
   z-index: -1;
