   text-align: center;
   margin-bottom: 30px;
   margin-top: -30px;
}

.outline {
   outline: 1px solid white;
}

.cursor {
   cursor: pointer;
}

.dp-none {
   display: none !important;
}

.dp-block {
   display: block !important;
}

.dp-flex {
   display: flex !important;
}

.pos-rel {
   position: relative;
}

.modal {
   display: flex;
   justify-content: center;
   align-items: center;
   flex-direction: column;
   position: fixed;
   top: 0;
   left: 0;
   bottom: 0;
   right: 0;
   background-color: #1d1f20;
   /* display: none; */
}

.close {
   position: absolute;
   top: 50px;
   right: 50px;
   color: white;
   font-size: 2em;
   /* background-color: white; */
   z-index: 100;
}

.noWrap {
   white-space: nowrap;
}

.notice {
   position: fixed;
   bottom: 10px;
   left: 50%;
   transform: translateX(-50%);
}

.ellipsis {
   overflow: hidden;
   text-overflow: ellipsis;
   white-space: nowrap;
}

.ellipsis:hover {
   overflow: initial;
   text-overflow: initial;
   white-space: initial;
   background-color: rgba(0, 0, 0, 0.5);
   position: fixed;
   width: 100%;
   left: 0;
   text-align: center;
   z-index: 10;
}

.previousResult.ellipsis:hover + .nowResult{
   margin-top: 51px;
}

.grayColor {
   background-color: #a7aab1;
}

.darkGrayColor {
   background-color: #363439;
}

.orangeColor {
   background-color: #fe8e1f;
   padding-bottom: 5px;
}

.clearFix::before,
.clearFix::after {
   content: "";
   display: table;
   clear: both;
}

